import React, { useState } from 'react'
import { Button, Col, Row } from 'react-bootstrap'
import TodoList from './TodoList';

function Todo({ todo, index, deleteTodo, editTodo }) {
  // console.log(index);
  return TodoList.map((todo, index)(




    <Row className="todo py-2 px-4 border">

       {/* DELETE */}
      <Button
        className="mx-2"
        size="sm"
        variant="danger"
        onClick={() => {
          deleteTodo(index)
        }}>Delete</Button>


      {/* EDIT */}
      <Button
        className="mx-2"
        size="sm"
        variant="warning"
        onClick={() => {
          editTodo(index, todo.text)
        }}
      >Edit</Button>


      {/* COMPLETE */}
        <Button
        className="mx-2"
        size="sm"
        variant="success"

        >Complete</Button>
    </Row>
  ));
};


export default Todo
