import React from 'react'
import { Button, Row } from 'react-bootstrap'
import TodoList from './TodoList';

function Todo({ todos, index, deleteTodo, editTodo }) {
  // console.log(index);
  return todos.map((todo, index) => (




    <Row className="todo py-2 px-4 border">



      <div>{todo.task}</div>







      <Button
        className="mx-2"
        size="sm"
        variant="danger"
        onClick={() => {
          deleteTodo(index)
        }}>Delete</Button>

      <Button
        className="mx-2"
        size="sm"
        variant="warning"
        onClick={() => {
          editTodo(index, todo.text)
        }}
      >Edit</Button>

        <Button
        className="mx-2"
        size="sm"
        variant="success"
        >Complete</Button>
    </Row>




  ));
};


export default Todo
