import React, {useState} from 'react'
import { Button, Row, Col } from 'react-bootstrap'
import TodoForm from './TodoForm'


function Todo({ todos, index, deleteTodo, completeTodo, updateTodo }) {


  const [edit, setEdit] = useState({
    id: null,
    task: ''
  })

  const submitUpdate = task => {
    updateTodo(edit.id, task)
    setEdit({
      id: null,
      task: ''
    });
  }

  if (edit.id) {
    return <TodoForm edit={edit} onSubmit={submitUpdate} />;
  }


  return todos.map((todo, index) => (
    <Row className="todo py-2 px-4 border" key={index}>

      <Col>{todo.task}</Col>


      <Col>
        <Button className="mx-2"
          size="sm"
          variant="success"
          onClic={() => {
            completeTodo(todo.id)
          }}>Complete
        </Button>

        <Button
          className="mx-2"
          size="sm"
          variant="warning"
          onClick={() => {
            updateTodo({ id: todo.id, value: todo.task })}}>Edit
        </Button>

        <Button
          className="mx-2"
          size="sm"
          variant="danger"
          onClick={() => {
            deleteTodo(index)
          }}>Delete
        </Button>     
      </Col>
    </Row>
  ));
};


export default Todo
