import React from 'react'
import { Button, Row, Col } from 'react-bootstrap'


function Todo({ todos, index, deleteTodo, completeTodo, updateTodo }) {
  return todos.map((todo, index) => (
    <Row className="todo py-2 px-4 border">

      <Col>{todo.task}</Col>
      <Col>






        <Button
          className="mx-2"
          size="sm"
          variant="danger"
          onClick={() => {
            deleteTodo(index)
          }}>Delete</Button>

        <Button
          className="mx-2"
          size="sm"
          variant="warning"
          onClick={() => {
            updateTodo({ id: todo.id, value: todo.task })}
          }>Edit</Button>



      </Col>
    </Row>




  ));
};


export default Todo
