import React from 'react';
import Header from './components/Header.js'
import Footer from './components/Footer.js'
import { BrowserRouter as Router } from 'react-router-dom'
import { Container, Col, Row } from 'react-bootstrap'
import { useState } from "react"



//=============== MAIN =============
function App() {



  return (
    <Router>
      <Header />
      <main className="py-3">

        <Container>
          <Row className="app d-flex justify-content-center">
            <Col xs={12} className="text-center py-4">TO DO LIST</Col>
            <Col className="todo-list">



              {todos.length < 1 &&
                <h3 className="text-center">You have no items in To Do List.</h3>
              }
            </Col>
          </Row>
          <Row className="py-4">
            <Col><TodoForm editTodo={editTodo} addTodo={addTodo} /></Col>
          </Row>
        </Container>



      </main>
      <Footer />
    </Router>
  );
}

export default App;
