// import React, { useState } from 'react'
import { Button, Col, Row } from 'react-bootstrap'

function Todo({ todo, index, deleteTodo, editTodo }) {
  // console.log(index);
  return (
    <Row className="todo py-2 px-4 border">
      <Col xs={2}>{index + 1}</Col>
      <Col >{todo.text}</Col>


       {/* DELETE */}
      <Button
        className="mx-2"
        size="sm"
        variant="danger"
        onClick={() => {
          deleteTodo(index)
        }}>Delete</Button>


      {/* EDIT */}
      <Button
        className="mx-2"
        size="sm"
        variant="warning"
        onClick={() => {
          editTodo(index, todo.text)
        }}
      >Edit</Button>


      {/* COMPLETE */}
        <Button
        className="mx-2"
        size="sm"
        variant="success"

        >Complete</Button>
    </Row>
  );
};


export default Todo
