import './App.css';
import React from 'react';
import Header from './components/Header.js'
import Footer from './components/Footer.js'
import { BrowserRouter as Router } from 'react-router-dom'
import { Container, Col, Row } from 'react-bootstrap'
import { useState } from "react"
import TodoForm from './components/TodoForm.js'
import Todo from './components/Todo.js'

//=============== MAIN =============
function App() {

  const [todos, setTodos] = useState([
    { text: "Read Peace and War by Leo Tolstoy" },
    { text: "Participate in CodeCamp" },
    { text: "Prepare dinner" }
  ]);


  function addTodo(todo) {
    const newTodos = ([todo, ...todos]);
    setTodos(newTodos);
  }

  // const addTodo = text => {
  //   const newTodos = [...todos, { text }];
  //   setTodos(newTodos);
  // }

  const deleteTodo = (todoIndex) => {
    const newTodos = todos.filter((_, index) => index !== todoIndex);
    setTodos(newTodos);
  }

  const editTodo = (todoIndex, text) => {
    const newTodos = [...todos]
    newTodos[todoIndex] = text
    setTodos(newTodos);
  }

  return (
    <Router>
      <Header />
      <main className="py-3">

        <Container>
          <Row className="app d-flex justify-content-center">
            <Col xs={12} className="text-center py-4">TO DO LIST</Col>
            <Col className="todo-list">

              {todos.map((todo, index) => {
                {/* console.log(index); */}
                return (
                  <Todo editTodo={editTodo} deleteTodo={deleteTodo} key={index} index={index} todo={todo}/>
                )
              })}


            {/* If there no items in the list display: */}
              {todos.length < 1 &&
                <h3 className="text-center">You have no items in To Do List.</h3>
              }
            </Col>
          </Row>
          <Row className="py-4">
            <Col><TodoForm editTodo={editTodo} addTodo={addTodo} /></Col>
          </Row>
        </Container>



      </main>
      <Footer />
    </Router>
  );
}

export default App;
