import TodoForm from './components/TodoForm.js'
import { Container, Col, Row } from 'react-bootstrap'




function TodoList() {

    const [todos, setTodos] = useState([
        { task: "Read Peace and War by Leo Tolstoy" },
        { task: "Participate in CodeCamp" },
        { task: "Prepare dinner" }
    ]);


    function addTodo(todo) {
        setTodos([todo, ...todos]);
    }

    const deleteTodo = (todoIndex) => {
        const newTodos = todos.filter((_, index) => index !== todoIndex);
        setTodos(newTodos);
  }



    return (
        <Container>
          <Row className="app d-flex justify-content-center">
            <Col xs={12} className="text-center py-4">TO DO LIST</Col>
            <Col className="todo-list">



              {/* {todos.length < 1 &&
                <h3 className="text-center">You have no items in To Do List.</h3>
              } */}
            </Col>
          </Row>
          <Row className="py-4">
            {/* <Col><TodoForm editTodo={editTodo} addTodo={addTodo} /></Col> */}
          </Row>
        </Container>
    )
}

export default TodoList;
